import React, { Component } from 'react';
import MainWindow from './Cmps/NBALeagueLeaders/MainWindow';
import MainBar from './Cmps/ToolBar/MainBar'

const axios = require('axios').default;


class App extends Component {
  state = {
    nba_leaguel_leaders: '',

  };

  componentDidMount() {
    this.start_localhost()
      .then(nba_leaguel_leaders => {this.setState({nba_leaguel_leaders: nba_leaguel_leaders})})
    }


  start_localhost = async () => {
    const response = await axios.get('http://localhost:5000')
    return response ;
  };

render() {
    return (
      <section className="App">
        <MainBar />
        <MainWindow nba_leaguel_leaders = {this.state.nba_leaguel_leaders}/>
      </section>
    );
  }
}

export default App;
