import React, { Component } from "react";
import AxiosFunction from "../../Functions/AxiosFunctions";
import RowFavoritesTable from "./RowFavoritesTable";

export default class FavoritesTable extends Component {
  state = {
    favorite_items: "",
    title: this.props.name,
    db: this.props.db,
  };

  async componentDidMount() {
    await AxiosFunction.get_favorite(this.state.db).then((list) => {
      this.setState({ favorite_items: list.data.express });
    });
  }

  render() {
    const favorite_items = this.state.favorite_items;
    return (
      <section>
        <h2>{this.state.title}</h2>
        <button
          onClick={async () => {
            await AxiosFunction.delete_all(this.state.db).then((list) => {
              this.setState({ favorite_items: list.data.express });
            });
          }}
        >
          Delete All
        </button>
        {favorite_items ? (
          favorite_items.length > 0 ? (
            <table>
              <tbody>
                {favorite_items.map((item, key) => (
                  <RowFavoritesTable
                    item={item}
                    key={key}
                    deleteItem={async (id) => {
                      // AxiosFunction.delete_item(id,this.state.db)
                      await (AxiosFunction.delete_item(id, this.state.db)).then(
                        (list) => {
                          this.setState({ favorite_items: list.data.express });
                        }
                      );
                    }}
                  />
                ))}
              </tbody>
            </table>
          ) : (
            console.log("no have result")
          )
        ) : (
          console.log("loading")
        )}
      </section>
    );
  }
}
