import React, { Component } from "react";
import MainWindow from "./Cmps/NBALeagueLeaders/MainWindow";
import MainBar from "./Cmps/ToolBar/MainBar";

const axios = require("axios").default;

class App extends Component {
  state = {
    season_leaders_players: "",
  };

  componentDidMount() {
    this.start_localhost().then((season_leaders_players) => {
      this.setState({
        season_leaders_players: season_leaders_players.data.express.items[0],
      });
    });
  }

  start_localhost = async () => {
    const response = await axios.get("http://localhost:5000");
    return response;
  };

  render() {
    return (
      <section className="App">
        <MainBar />
        <MainWindow season_leaders_players={this.state.season_leaders_players} />
      </section>
    );
  }
}

export default App;
