// -------------------- Import --------------------
import React, { Component } from "react";
import MainWindow from "./Cmps/PlayersAndNBALeagueLeaders/MainWindow";
import MainBar from "./Cmps/ToolBar/MainBar";
import AxiosFunction from "./Functions/AxiosFunctions";
import OpenItemWindows from "./Cmps/PlayersAndNBALeagueLeaders/OpenItemWindows";

// -------------------- sittings --------------------
const axios = require("axios").default,
      port = "http://localhost:5000"

// -------------------- NBA App --------------------

class App extends Component {
  // State
  state = {
    season_leaders_players: "",
    nab_league_leaders: "",
    open_item_window: "",
  };

  // Component Did Mount
  componentDidMount() {
    this.start_localhost().then((season_leaders_players) => {
      this.setState({
        season_leaders_players: season_leaders_players.data.nba_status.items[0],
        nab_league_leaders: season_leaders_players.data.nba_status.items[1],
      });
    });
  }

  // Start localhost-locallhost
  start_localhost = async () => {
    const response = await axios.get(port);
    return response;
  };

  render() {
    return (
      <section className="App">
        {/* Main Bar */}
        <MainBar />
        <div className="season_leaders_players">
          {/* MainWindow - Favorites Players*/}
          <MainWindow
            items={this.state.season_leaders_players.items}
            title={this.state.season_leaders_players.title}
            like={(like_data) => {
              AxiosFunction.post_favorite(like_data, "FavoritesPlayers");
            }}
            openItem={(selected_item) => {
              axios.get(`${port}/players/${selected_item.PLAYER_ID}`)

            }}
          />
        </div>
        <div className="nab_league_leaders">
          {/* MainWindow - NBA League Leaders*/}
          <MainWindow
            items={this.state.nab_league_leaders.items}
            title={this.state.nab_league_leaders.title}
            like={(like_data) => {
              AxiosFunction.post_favorite(like_data, "FavoritesTeams");
            }}
          />
        </div>
         {/*Open Item Windows */}
        <div>
          {this.state.open_item_window ? (
            <OpenItemWindows selected_item={this.state.open_item_window} />
          ) : (
            ""
          )}
        </div>
      </section>
    );
  }
}

export default App;
