import React, { Component } from "react";
import MainWindow from "./Cmps/PlayersAndNBALeagueLeaders/MainWindow";
import MainBar from "./Cmps/ToolBar/MainBar";
import AxiosFunction from "./Functions/AxiosFunctions";
import full_like_icon from "../src/Assets/Icons/full_heart.png";


const axios = require("axios").default,
  port = "http://localhost:5000";

class App extends Component {
  state = {
    season_leaders_players: "",
    nab_league_leaders: "",
    favorites_players:[],
    favorites_teams:[]
  };

  async componentDidMount() {
    let favorites_players_list = []
    let favorites_teams_list = []

    // function testq (list){
    //   console.log(list);
    // }

    // testq(favorites_players_list)
    // // test = (list)=>{console.log(list)}

    async function favorites_list_by_id(favorites_list,db,state_list){
      await AxiosFunction.get_favorite(db).then((list) => {
        (list.data.favorit_list).map((favorit)=>{favorites_list.push(favorit.PLAYER_ID || favorit.TEAM_ID) })
        this.setState({ [state_list]: favorites_list });
      });
    }

    // favorites_list_by_id = async(favorites_list,db,state_list) =>{

    //   await AxiosFunction.get_favorite(db).then((list) => {
    //     (list.data.favorit_list).map((favorit)=>{favorites_list.push(favorit.PLAYER_ID || favorit.TEAM_ID) })
    //     this.setState({ [state_list]: favorites_list });
    //   });
    // }

    favorites_list_by_id(favorites_players_list,"FavoritesPlayers",favorites_players)
    favorites_list_by_id(favorites_teams_list,"FavoritesTeams",favorites_teams)

    // await AxiosFunction.get_favorite("FavoritesPlayers").then((list) => {
    //   (list.data.favorit_list).map((favorit)=>{favorites_list.push(favorit.PLAYER_ID) })
    //   this.setState({ favorites_players: favorites_list });
    // });

    // await AxiosFunction.get_favorite("FavoritesTeams").then((list) => {
    //   this.setState({ favorites_teams: list.data.favorit_list });
    // });



    this.start_localhost().then((season_leaders_players) => {
      this.setState({
        season_leaders_players: season_leaders_players.data.nba_status.items[0],
        nab_league_leaders: season_leaders_players.data.nba_status.items[1],
      });
    });
  }




  start_localhost = async () => {
    const response = await axios.get(port);
    return response;
  };

  like = async(like_data,id,Favorit)=>{
      document.querySelector(`.${id}`).src = full_like_icon;
      await AxiosFunction.post_favorite(
        like_data,
        Favorit
      ).then((result_exists) => {
        console.log(result_exists.data.exists_item_in_favorite);
      });
    }


  render() {
    return (
      <section className="App">
        <MainBar />
        <div className="lebron_james">
          <h1>The Satus App of the NBA</h1>
        </div>
        <div className="nba_lists">
          <div className="season_leaders_players">
            <MainWindow
              favorites_teams = {this.state.favorites_players}
              items={this.state.season_leaders_players.items}
              title={this.state.season_leaders_players.title}
              like={async (like_data, id)=>{this.like(like_data, id, "FavoritesPlayers")}}
            />
          </div>
          <div className="nab_league_leaders">
            <MainWindow
              favorites_teams = {this.state.favorites_teams}
              items={this.state.nab_league_leaders.items}
              title={this.state.nab_league_leaders.title}
              like={async (like_data, id)=>{this.like(like_data, id, "FavoritesTeams")}}
            />
          </div>
        </div>
      </section>
    );
  }
}

export default App;
